public async void AddLabel()
{
   ShapeAnnotation annotation = new ShapeAnnotation { Content = "Annotation" };
   await (diagram.Nodes[0].Annotations as DiagramObjectCollection<ShapeAnnotation>).AddAsync(annotation);
}


